const dummyUser = {
    nickname: '제로초',
    Post: [],
    Followings: [],
    Followers: [],
  };

// 초기값
export const intialState = {
    isLoggedIn: false,
    user: {
        
        Post: [],
        Followings: [],
        Followers: [],
    },
    nickname: '제로초',
    
};

export const LOG_IN = 'LOG_IN'; // 액션 이름
export const LOG_OUT = 'LOG_OUT'; // 액션 이름

// 액션의 값
export const loginAction = {
    type: LOG_IN,
    data : {
        nickname: '정선재',
    },
};

export const logoutAction = {
    type: LOG_OUT,
}


const reducer = (state = intialState, action) => {

     switch(action.type)
     {
         case LOG_IN: {
             return {
                 ...state,
                 isLoggedIn: true,
                 user: action.data,
             }
         }
         case LOG_OUT : {
             return {
                 ...state,
                 isLoggedIn: false,
                 user: null,
             }
         }
         default :{
            return {
                ...state,
            }
        }
     }

}

export default reducer;
