import {all, fork,takeLatest,takeEvery} from "redux-saga/effects";
import { LOG_IN,LOG_IN_SUCCESS, LOG_IN_FAILURE } from "../reducers/user";

function loginAPI() {
// 서버에 요청
}

function* login(){
    try {
        yield call(loginAPI);
        yield put({
            type:LOG_IN_SUCCESS
        })
    }
    catch (e)
    {
        console.error(e);
        yield put({
            type:LOG_IN_FAILURE
        })
    }
}

function* watchLogin() {

    yield takeEvery(LOG_IN, login)

}

export default function* userSaga() {
    yield all([

        fork(watchLogin),

    ]);
}
