import React from 'react' // 'Next는 따로 import해주지 않아도 되지만 구조상 사용함'
import Link from 'next/link';
import { Input,Form, Button, Card,Icon, Avatar } from 'antd';

const dummy = {
    isLoggedIn : true,
    imagePaths : [

    ],
    mainPosts: []
};


const Home = () => {
    return (
       <div>
           {dummy.isLoggedIn && <Form encType="multipart/form-data"> 
                <Input.TextArea MaxLength={140} placeholder="글을 입력" />
                <div>
                    <Input type="file" multiple hidden/>
                    <Button>이미지 업로드</Button>
                    <Button type="primary" style={{float:'right'}} htmlType="submit">버튼</Button>
                </div>

                <div>
                    {dummy.imagePaths.map((v,i)=> {
                        return (
                            <div key={i} style={{display:'inline-block'}} >
                                <img src={'http://localhost:3065/'+ v} style={{width:'250px'}} alt={v} />
                                <Button>제거</Button>
                            </div>
                        )
                    })}
                </div>
                {mainPosts.map((c)=> {
                    return (
                        <Card 
                        key={+c.createAt} 
                        cover={c.img && <img alt="example" src={c.img}/>}
                        actions={[
                            <Icon type="retweet" key="retweet"/>,
                            <Icon type="heart" key="heart"/>,
                            <Icon type="message" key="message"/>,
                            <Icon type="ellipsis" key="ellipsis"/>,
                        ]}
                        extra={<Button>팔로우</Button>}
                        >
                            <Card.Meta
                                avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
                                title={c.User.nickname}
                                description={c.content}
                                />
                        </Card>
                    );
                })}
            </Form>}
       </div>
    );
};

export default Home;
